const valutes=document.querySelectorAll(".valute"),courseValue=document.querySelectorAll(".course"),times=document.querySelectorAll(".time"),prices=document.querySelectorAll(".level-price");async function removeClass(e){e.classList.remove("side-off"),e.classList.add("side-on")}let value=0;for(let e of prices)0===value?(value+=1,setTimeout((()=>{removeClass(e)}),100)):1===value?(value+=1,setTimeout((()=>{removeClass(e)}),500)):setTimeout((()=>{removeClass(e)}),900);function changeCourse(e){for(let t of courseValue)if("$"==e){const e=(89*Number(t.textContent)).toFixed(2);t.textContent=e}else if("₽"==e){const e=(Number(t.textContent)/89*1).toFixed(2);t.textContent=e}else{const e=(Number(t.textContent)/1).toFixed(2);t.textContent=e}}for(let e of valutes)e.addEventListener("click",(function(e){const t=e.target.textContent;for(let e of valutes)e.textContent="$"==t?"₽":"₽"==t?"€":"$";changeCourse(t)}));for(let e of times)e.addEventListener("click",(function(e){if("/Months"==e.target.textContent){for(z of times)z.textContent="/Day";for(let e of courseValue)console.log(e.textContent),e.textContent=(Number(e.textContent)/30).toFixed(2)}else{for(z of times)z.textContent="/Months";for(let e of courseValue)console.log(e.textContent),e.textContent=(30*Number(e.textContent)).toFixed(2)}}));